                null, // General is null
                Team.Blue,
                new string[] { "spawn_with_ultimate_ready", "infinite_ultimate_duration" },
                new object[] { true, true }
            )
            // Give red 200% movement speed and 50% movement gravity.
            , new SetHero(
                null,
                Team.Red,
                new string[] { "movement_speed", "movement_gravity" },
                new object[] { 200, 50 }
            )
        );
    }
}

